import React, {Component} from 'react';

export default ({selectedBuilding, data}) => {

	if(selectedBuilding == 0){
		return (
			<div>
				<p>
					{' '}
					<i>Click on a name to view more information</i>
				</p>
			</div>
		);
	}
	else{
		const index = data.map(building => {
			return building.id;
		})
		.indexOf(selectedBuilding);

		const myBuilding = data[index];

		const coordUndef = myBuilding.coordinates == undefined;
		console.log(myBuilding);
		//const id = this.props.selectedBuilding;
		return (

			<div>
				<p>
					{' '}
					<i>Building info</i>
					<div style= {{marginTop: 20}}>
						<ul><strong>Name: </strong>{myBuilding.name}</ul>
						<ul><strong>Code: </strong>{myBuilding.code}</ul>


						<ul><strong>Address: </strong>{myBuilding.address}</ul>

						{/* print coordinates only if they are defined */}
						{!myBuilding.coordinates == undefined ? (
							<ul><strong>Coordinates:</strong>
								<li><em>latitude:</em> {myBuilding.coordinates.latitude}</li>
								<li><em>longitude:</em> {myBuilding.coordinates.longitude}</li>
							</ul>
							) : (console.log("not found"))
						}
					</div>


				</p>
			</div>
		);
	}
}
